import React from 'react';
import {connect} from 'react-redux';
import {Link, Redirect} from 'react-router-dom';
import './User.css';
import {deleteUsers} from '../../Actions';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Slide from '@material-ui/core/Slide';
import Button from '@material-ui/core/Button';

const Transition = React.forwardRef(function Transition(props, ref) {
	return <Slide direction="up" ref={ref} {...props} />;
});

function UserProfile({user, deleteUsers}) {
	const [open, setOpen] = React.useState(false);

	const handleClickOpen = () => {
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};
	return (
		<div>
			{user.id ? (
				<div className="userProf">
					<div id="user">
						<div>
							{user.admin ? (
								<img
									style={{
										marginLeft: '40px',
										width: '76%',
										borderRadius: '140px',
										marginBottom: '5px',
										marginTop: '10px',
									}}
									src="https://www.vasport.vn/public/admin/img/avatar.png"
								/>
							) : (
								<img
									style={{borderRadius: '140px', marginBottom: '5px', marginTop: '10px'}}
									src="data:image/png;base64,"></img>
							)}
						</div>
						<div className="userData">
							{user.admin ? <span>ADMINISTRADOR: </span> : <span>CLIENTE:</span>}
							<h1>
								{user.nombre} {user.apellido}
							</h1>
							<p> E-mail: {user.email} </p>
							<Link to={`/users/${user.id}/orders`}>
								<button className="compra">Mis Compras</button>
							</Link>
							<Link to="/RestablecerContraseña">
								<button className="changePass">Cambiar contraseña</button>
							</Link>
							<button className="deleteAcc" onClick={handleClickOpen}>
								Eliminar Cuenta
							</button>
							<Dialog
								open={open}
								TransitionComponent={Transition}
								keepMounted
								onClose={handleClose}
								aria-labelledby="alert-dialog-slide-title"
								aria-describedby="alert-dialog-slide-description">
								<DialogTitle id="alert-dialog-slide-title">{'Eliminar cuenta'}</DialogTitle>
								<DialogContent>
									<DialogContentText id="alert-dialog-slide-description">
										¿Estas seguro que quieres eliminar tu cuenta?
									</DialogContentText>
								</DialogContent>
								<DialogActions>
									<Button onClick={handleClose} color="primary">
										Cancelar
									</Button>
									<Button
										onClick={() => {
											deleteUsers(user.id);
											handleClose();
										}}
										color="primary">
										Aceptar
									</Button>
								</DialogActions>
							</Dialog>
						</div>
					</div>
				</div>
			) : (
				<Redirect to="/sign_up" />
			)}
		</div>
	);
}
function mapStateToProps(state) {
	return {
		user: state.user,
	};
}

export default connect(mapStateToProps, {deleteUsers})(UserProfile);
